DROP TABLE IF EXISTS translations;
CREATE TABLE translations (
    transCode VARCHAR PRIMARY KEY,
    parser VARCHAR,
    name VARCHAR,
    sourceUrl VARCHAR,
    copyright VARCHAR,
    rtl BOOLEAN
);
BEGIN;






















COMMIT;



DROP TABLE IF EXISTS translationLangs;
CREATE TABLE translationLangs (
    transCode VARCHAR,
    langCode VARCHAR,
    name VARCHAR,
    PRIMARY KEY (transCode, langCode)
);
BEGIN;






















COMMIT;



DROP TABLE IF EXISTS books;
CREATE TABLE books (
    transCode VARCHAR,
    langCode VARCHAR,
    bookCode VARCHAR,
    bookNo INTEGER,
    bookName VARCHAR,
    PRIMARY KEY (transCode, langCode, bookCode)
);
BEGIN;
































































































COMMIT;



DROP TABLE IF EXISTS chapterSize;
CREATE TABLE chapterSize (
    transCode VARCHAR,
    bookCode VARCHAR,
    chapterNo INTEGER,
    verseCount INTEGER,
    PRIMARY KEY (transCode, bookCode, chapterNo)
);
BEGIN;






















































COMMIT;

VACUUM;
